import { Component } from '@angular/core';
import { User } from '../REGapi/models';

@Component({
  selector: 'app-reglog',
  templateUrl: './reglog.component.html',
  styleUrls: ['./reglog.component.css']
})
export class ReglogComponent {
  name :string = '';
  mail :string ='';
  pas : string = '';
  reg(){
    let curr:User = {
      name :this.name
      ,
      login : this.mail,
      password : this.pas
    };
    console.log(curr);
    this.name = '';
    this.mail ='';
    this.pas = '';
  }
  log(){
    let curr:User = {
      name :this.name
      ,
      login : this.mail,
      password : this.pas
    };
    console.log(curr);
    this.name = '';
    this.mail ='';
    this.pas = '';
  }
}
<div class="log">

    Имя
  <input type="text"  [(ngModel)]="name"> <br>
  E-mail
  <input type="email"  [(ngModel)]="mail"> <br>
  Пароль
  <input type="password"  [(ngModel)]="pas"> <br>
  <button (click)="reg()" >Регистрация</button> <button (click)="log()"> Войти</button>

</div>
