import { Component } from '@angular/core';
import { User } from '../api/models';
import { USService } from '../api/services';
import { InMemoryUsers } from '../memory-us.service';
import { CarsComponent } from '../cars/cars.component';
@Component({
  selector: 'app-reglog',
  templateUrl: './reglog.component.html',
  styleUrls: ['./reglog.component.css']
})
export class ReglogComponent {

  users : User[] = [];
  autorized: boolean = false;
  constructor(private serv : USService,){}//protected carcomp : CarsComponent ){}
  ngOnInit(): void {
this.get();

  }
  get(){
    this.serv.getAllUsers().subscribe((us) => {
      this.users = us;
      console.log(us);
    });
  }
  name :string = '';
  mail :string ='';
  pas : string = '';
  reg(){
    let curr:User = {
      name :this.name,
      login : this.mail,
      password : this.pas
    };
    console.log(curr);
    this.name = '';
    this.mail ='';
    this.pas = '';
    this.serv.addUs({body:curr});
    this.get();
  }
  log(){
    let curr:User = {
      name :this.name,
      login : this.mail,
      password : this.pas
    };
    console.log(curr);
    this.name = '';
    this.mail ='';
    this.pas = '';
    for(let i = 0; i< this.users.length;i++){
      let element = this.users[i];
      if(curr.login == element.login && curr.name == element.name && curr.password == element.password){
       alert("УСПЕШНО");
        this.autorized = true;
        this.showdata();
        break;
       }
    }
  }
  showdata(){
   // this.carcomp.request();
  }
}
