import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Stomp, Client, StompSubscription, IMessage} from '@stomp/stompjs';
import * as SockJS from 'sockjs-client';
@Injectable({
  providedIn: 'root'
})
export class WebsocketService {
  private stompClient;

  constructor() {
     const socket = new SockJS('http://localhost:8082/websocket');
    this.stompClient = Stomp.over(socket);
    // new Client({
    //   webSocketFactory: () => socket,
    //   reconnectDelay: 5000,
    //   heartbeatIncoming:0,
    //   heartbeatOutgoing: 0,
    // });
    this.stompClient.activate();
  }

  connect(): Observable<any> {
    return new Observable(observer => {
      this.stompClient.subscribe('/topic/data', (message : IMessage) => {
        console.log(message);
        observer.next(JSON.parse(message.body));
    });
    });
  }

  sendData(data: any): void {

    // this.stompClient.publish({
    //   destination: '/app/updateData',
    //   body: JSON.stringify(data)
    // });
    this.stompClient.send('/app/updateData', {}, JSON.stringify(data));
  }
}
